import React from "react";
import Kakao_icon from "../../../Asset/image/kakao_login_medium_narrow.png";
// import Kakao_icon from "../../../Asset/image/free-icon-kakao-talk-2111466.png";
import { Button, Image } from "@chakra-ui/react";
import styled from "styled-components";
import KakaoLogin from "react-kakao-login";

const KakaoLoginButton = ({ onSuccess, onFailure }) => {
  const responseKaKao = (response) => {
    console.log(response); // 로그인 결과 확인
    if (response.profile) {
      // 로그인 성공 처리
      onSuccess(response.profile);
    } else {
      // 로그인 실패 처리
      onFailure(response);
    }
  };

  return (
    <KakaoLogin
      token=""
      onSuccess={responseKaKao}
      onFailure={responseKaKao}
      render={({ onClick }) => (
        <Image
          src={Kakao_icon}
          alt="My Image"
          width="12rem"
          my="1rem"
          onClick={onClick}
          _hover={{
            cursor: "pointer",
          }}
        />

        // <StyledButton onClick={onClick}>
        //   <img src={Kakao_icon} alt=""></img>
        //   카카오톡 로그인하기
        // </StyledButton>
      )}
    />
  );
};

export default KakaoLoginButton;

const StyledButton = styled(Button)`
  background-color: #ffff3b;
  height: 2.3rem;
  width: 16rem;
  border-radius: 10px;
  margin: 0.5rem 0px;
  border-color: transparent;
  font-size: 0.8rem;
  font-weight: 550;
  color: #412723;
  z-index: 0;
  img {
    margin: 0px 10px;
    height: 1.4rem;
    width: 1.4rem;
  }
  &:hover {
    background-color: #ffeb3b;
    color: #000000;
    /* 호버 상태에서의 스타일을 정의합니다 */
  }
`;
